package basic_003;
// write a java code to print odd numbers from 60 to 70
public class A004_forloop_odd_60_70 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 60; i <=70; i++) {
			if (i%2!=0) {
				System.out.println(i);
				
			}
			
		}

	}

}
