package basic_003;
//write a java code to print even numbers from 30 to 20

public class A005_forloop_even_30_20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 30; i >= 20; i--) {
			if (i%2==0) {
				System.out.println(i);
				
			}
			
		}

	}

}
