package basic_003;
// write a java code to print odd numbers from 660 to 520
public class A006_forloop_odd_660_520 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 660; i >=520; i--) {
			if (i%2!=0) {
				System.out.println(i);
				
			}
			
		}

	}

}
