package basic_003;
//write a java code to print even from 770to 460
public class A007_forloop_even_770_460 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 770; i>=460; i--) {
			if (i%2==0) {
				System.out.println(i);
				
			}
			
		}
		

	}

}
